import React, { useState } from 'react';
import {
    ChakraProvider,
    Box,
    Container,
    Button,
    Center,
    Image,
    SimpleGrid,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    ModalFooter,
    useDisclosure,
    Flex

} from '@chakra-ui/react';

import theme from '../../theme/theme';



const Experience = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const [count, setCount] = useState(0);
    const handle = (prop) => {
        setCount(prop);
        onOpen();
    };

    const workList = [{
        id: 0,
        imageUrl: '/images/skills/upython.png',
        imageAlt: 'MicroPython Image',
        skillsText: 'MicroPython'
    },
    ]

    const workModal = [{
        id: 1,
        title: 'MicroPython',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    ]

    return (
        <ChakraProvider theme={theme}>
            <Container
                maxW='container.xxl'
                bgColor='#D7D7D980'
                pb={['3vw', '1vw']}
                px={['3vw', '5vw']}
                id='experience'
            >

                <Center>
                    <Center
                        minW={['75vw', '10vw']}
                        bgColor='white'
                        borderRadius={['75vw', '10vw']}
                        border='2px'
                        color='Black'
                        padding='1vw'
                        margin='1vw'
                        fontWeight={'bold'}
                    >
                        Work Experience
                    </Center>
                </Center>
                <Flex columns={1} spacing='10px' >
                    {workList.map((property) => (
                        <Box maxW='sm' borderWidth='1px' borderRadius='lg' overflow='hidden' bgColor='#FFFFFF'>

                            <Center>
                                <Image height={{ 'base': '70px', 'sm': '50px', 'md': '100px' }} mt={{ 'base': '4px', 'sm': '3px', 'md': '10px' }} src={property.imageUrl} alt={property.imageAlt} />
                            </Center>


                            <Box p='3'>
                                <Center>
                                    <Box
                                        mt='1'
                                        fontWeight='semibold'
                                        as='h4'
                                        lineHeight='tight'
                                        noOfLines={1}
                                    >
                                        <Button bgColor={'inherit'} onClick={() => handle(property.id)}>
                                            {property.skillsText}
                                        </Button>

                                    </Box>
                                </Center>
                                <Modal
                                    isCentered
                                    onClose={onClose}
                                    isOpen={isOpen}
                                    motionPreset='slideInBottom'
                                    blockScrollOnMount={true}
                                    bgColor='#FFFFFF'
                                >
                                    <ModalOverlay
                                        bg='none'
                                        backdropFilter='blur(10px) '
                                        backdropInvert='80%'
                                        backdropBlur='2px' />
                                    <ModalContent>
                                        <ModalHeader>{workModal[parseInt(count)].title}</ModalHeader>
                                        <ModalCloseButton />
                                        <ModalBody>
                                            {workModal[parseInt(count)].experience}
                                            {workModal[parseInt(count)].id}
                                        </ModalBody>
                                        <ModalFooter>
                                            <Button colorScheme='blue' mr={3} onClick={onClose}>
                                                Close
                                            </Button>
                                            <Button variant='ghost'>Secondary Action</Button>
                                        </ModalFooter>
                                    </ModalContent>
                                </Modal>


                            </Box>

                        </Box>


                    ))}

                </Flex>

            </Container >
        </ChakraProvider >
    );
}

export default Experience;
