import React from 'react';
import {
    ChakraProvider,
    Box,
    Text,
    Container,
    Flex,
    Stack,
    Button,
    useColorMode,
    Center,
    Image,

} from '@chakra-ui/react';

import {
    EmailIcon,
    ArrowForwardIcon,
} from '@chakra-ui/icons'

const LandingPage = () => {
    const [show, setShow] = React.useState(false);
    const handleToggle = () => setShow(!show);
    const { colorMode, toggleColorMode } = useColorMode();
    const primaryColor = colorMode === "dark" ? "white" : "black";
    const secondaryColor = colorMode === "dark" ? "gray" : "gray";
    const bgColor = colorMode === "dark" ? "gray.400" : "white";

    return (
        <Container
            maxW='container.xl'
            // bg='pink'
            color='#262626'
        >
            <Flex mb={{ 'sm': '5vh', 'md': '9vh' }} color='white' minHeight='100%' bgColor={bgColor} flexDirection={{ 'base': 'column', 'sm': 'column', 'md': 'row' }}>
                <Box
                    flex='1'
                    // bg='white' 
                    minHeight='7vh'
                    justifyContent={'end'}>

                    <Text
                        color={secondaryColor}
                        lineHeight={{ 'sm': '8vh', 'md': '9vh' }}
                        align='initial'
                        fontSize={{ 'sm': '36', 'md': '48' }}
                        fontWeight='bold'
                        textShadow='2px 2px #AAAAAA80'
                    //bgColor={'blue'}
                    >
                        I am
                    </Text>
                    <Text
                        color={primaryColor}
                        lineHeight={{ 'sm': '7vh', 'md': '8vh' }}
                        align='initial' fontSize={{ 'sm': '36', 'md': '48' }}
                        fontWeight={700}
                        textShadow='2px 2px #BBBBBB90'

                    //bgColor='green.200'
                    >
                        Samuel YAKOBUS
                    </Text>
                    <Stack direction='row' justify={'center'} spacing={{ 'sm': '7vh', 'md': '8vh' }} paddingTop={{ 'sm': '12vh', 'md': '15vh' }}>
                        <Button leftIcon={<EmailIcon />} colorScheme='teal' variant='solid'>
                            My/CV Resume
                        </Button>
                        <Button rightIcon={<ArrowForwardIcon />} colorScheme='teal' variant='outline'>
                            Get in touch
                        </Button>
                    </Stack>
                </Box>
                <Box flex='1' >
                    <Center>
                        <Image maxWidth={{ 'sm': '40vh', 'md': '20vh' }} boxShadow='12px 12px 2px 1px rgba(55, 65, 81, 1)' src={'/images/langdingPage.png'} alt="Samuel" />
                    </Center>
                </Box>
            </Flex>
        </Container >

    );
}

export default LandingPage;
