import React from 'react';
import {
    ChakraProvider,
    Box,
    Text,
    Container,
    Flex,
    Stack,
    Button,
    useColorMode,
    Center,
    Image,
    boxShadow,
    Card,
    Heading,
    CardBody,
    Divider,
    CardFooter,
    ButtonGroup,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    ModalFooter,
    useDisclosure,
    useState,

} from '@chakra-ui/react';
import theme from '../../theme/theme';


const ModalSkill = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const [count, setCount] = useState(0);
    const handleIndex = (number) => {
        setCount(number);
    };

    const skillsModal = [{
        id: 1,
        title: 'MicroPython',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 2,
        title: 'Arduino',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 3,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 4,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 5,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 6,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 7,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 8,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 9,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 10,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },]
    return (
        <ChakraProvider theme={theme}>
            <Modal
                isCentered
                onClose={onClose}
                isOpen={isOpen}
                motionPreset='slideInBottom'
                blockScrollOnMount={true}
            >
                <ModalOverlay
                    bg='none'
                    backdropFilter='blur(10px) '
                    backdropInvert='80%'
                    backdropBlur='2px' />
                <ModalContent>
                    <ModalHeader>{skillsModal[{ count }].title}</ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>
                        {skillsModal[{ count }].experience}
                        {skillsModal[{ count }].id}
                    </ModalBody>
                    <ModalFooter>
                        <Button colorScheme='blue' mr={3} onClick={onClose}>
                            Close
                        </Button>
                        <Button variant='ghost'>Secondary Action</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
        </ChakraProvider>
    );
}

export default ModalSkill;
