import React, { useState } from 'react';
import {
    ChakraProvider,
    Box,
    Text,
    Container,
    Flex,
    Stack,
    Button,
    useColorMode,
    Center,
    Image,
    boxShadow,
    Card,
    Heading,
    CardBody,
    Divider,
    CardFooter,
    ButtonGroup,
    Badge,
    SimpleGrid,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    ModalFooter,
    useDisclosure,
} from '@chakra-ui/react';

import { StarIcon } from '@chakra-ui/icons';
import theme from '../../theme/theme';
import ModalSkill from './modal';



const Skills = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const [count, setCount] = useState(0);
    function handle() {
        handleIncrement();
        onOpen();
    }
    const handleIncrement = () => {
        setCount(0);
    };

    const skillList = [{
        id: 0,
        imageUrl: '/images/skills/upython.png',
        imageAlt: 'MicroPython Image',
        skillsText: 'MicroPython'
    },
    {
        id: 1,
        imageUrl: '/images/skills/arduino.png',
        imageAlt: 'Arduino Logo',
        skillsText: 'Arduino'
    },
    {
        id: 2,
        imageUrl: '/images/skills/python.png',
        imageAlt: 'Python Logo',
        skillsText: 'Python'
    },
    {
        id: 3,
        imageUrl: '/images/skills/javascript.png',
        imageAlt: 'JavaScript',
        skillsText: 'JavaScript'
    },
    {
        id: 4,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'C++',
        skillsText: 'C++'
    },
    {
        id: 5,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'Chakra UI',
        skillsText: 'Chakra UI'
    },
    {
        id: 6,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'Git',
        skillsText: 'Git'
    },
    {
        id: 7,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'Multilingual',
        skillsText: 'Multilingual'
    },
    {
        id: 8,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'STM32',
        skillsText: 'STM32 Development'
    },
    {
        id: 9,
        imageUrl: '/images/skills/c++.svg',
        imageAlt: 'Microsoft Office',
        skillsText: 'Word, Excel, PowerPoint'
    },
    ]

    const skillsModal = [{
        id: 1,
        title: 'MicroPython',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 2,
        title: 'Arduino',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 3,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 4,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 5,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 6,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 7,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 8,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 9,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },
    {
        id: 10,
        title: 'Python',
        explanation: 'Python is a popular programming language used in modern day data processing and web development.',
        experience: 'ECAPA-TDNN FYP, Personal Projects, Etc.',
        website: 'python.org'
    },]

    return (
        <ChakraProvider theme={theme}>
            <Container
                maxW='container.xl'
                bgColor='inherit'
                pb={['3vw', '1vw']}
                px={['3vw', '5vw']}
                id='skills'
            >

                <Center>
                    <Center
                        minW={['75vw', '10vw']}
                        bgColor='white'
                        borderRadius={['75vw', '10vw']}
                        border='2px'
                        color='Black'
                        padding='1vw'
                        margin='1vw'
                        fontWeight={'bold'}
                    >
                        Skills
                    </Center>
                </Center>
                <SimpleGrid columns={[2, 3, 5]} spacing='10px' >
                    {skillList.map((property) => (
                        <Box maxW='sm' borderWidth='1px' borderRadius='lg' overflow='hidden' bgColor='#EEEEEE80'>

                            <Center>
                                <Image height={{ 'base': '70px', 'sm': '50px', 'md': '100px' }} mt={{ 'base': '4px', 'sm': '3px', 'md': '10px' }} src={property.imageUrl} alt={property.imageAlt} />
                            </Center>


                            <Box p='3'>
                                <Center>
                                    <Box
                                        mt='1'
                                        fontWeight='semibold'
                                        as='h4'
                                        lineHeight='tight'
                                        noOfLines={1}
                                    >
                                        <Button bgColor={'inherit'} onClick={{ onOpen }, () => setCount(property.id)}>
                                            {property.skillsText}
                                            {count}
                                        </Button>

                                    </Box>
                                </Center>
                                <Modal
                                    isCentered
                                    onClose={onClose}
                                    isOpen={isOpen}
                                    motionPreset='slideInBottom'
                                    blockScrollOnMount={true}
                                >
                                    <ModalOverlay
                                        bg='none'
                                        backdropFilter='blur(10px) '
                                        backdropInvert='80%'
                                        backdropBlur='2px' />
                                    <ModalContent>
                                        <ModalHeader>{skillsModal[parseInt(count)].title}</ModalHeader>
                                        <ModalCloseButton />
                                        <ModalBody>
                                            {skillsModal[parseInt(count)].experience}
                                            {skillsModal[parseInt(count)].id}
                                        </ModalBody>
                                        <ModalFooter>
                                            <Button colorScheme='blue' mr={3} onClick={handle}>
                                                Close
                                            </Button>
                                            <Button variant='ghost'>Secondary Action</Button>
                                        </ModalFooter>
                                    </ModalContent>
                                </Modal>


                            </Box>

                        </Box>


                    ))}
                    {/* 
                    {skillsModal.map((property) => (
                        <Modal
                            isCentered
                            onClose={onClose}
                            isOpen={isOpen}
                            motionPreset='slideInBottom'
                            blockScrollOnMount={true}
                        >
                            <ModalOverlay
                                bg='none'
                                backdropFilter='blur(10px) '
                                backdropInvert='80%'
                                backdropBlur='2px' />
                            <ModalContent>
                                <ModalHeader>{property.title}</ModalHeader>
                                <ModalCloseButton />
                                <ModalBody>
                                    {property.experience}
                                    {property.id}
                                </ModalBody>
                                <ModalFooter>
                                    <Button colorScheme='blue' mr={3} onClick={onClose}>
                                        Close
                                    </Button>
                                    <Button variant='ghost'>Secondary Action</Button>
                                </ModalFooter>
                            </ModalContent>
                        </Modal>))} */}
                </SimpleGrid>

            </Container >
        </ChakraProvider >
    );
}

export default Skills;
