import React, { useState } from 'react';
import {
    ChakraProvider,
    Box,
    Container,
    Button,
    Center,
    Image,
    SimpleGrid,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    ModalFooter,
    useDisclosure,
    Flex,
    VStack,
    Text,
    UnorderedList,
    ListItem,
    useClipboard,

} from '@chakra-ui/react';

import theme from '../../theme/theme';



const GetInTouch = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const [count, setCount] = useState(0);


    const placeholder = 'text to be copied...'
    const { onCopy, value, setValue, hasCopied } = useClipboard('')
    return (
        <ChakraProvider theme={theme}>
            <Container
                maxW='container.xxl'
                bgColor='#ffffff'
                pb={['3vw', '1vw']}
                px={['3vw', '5vw']}
                id='getintouch'
            >

                <Center>
                    <Center
                        minW={['75vw', '10vw']}
                        bgColor='white'
                        borderRadius={['75vw', '10vw']}
                        border='2px'
                        color='Black'
                        padding='1vw'
                        margin='1vw'
                        fontWeight={'bold'}
                        fontFamily={'heading2'}
                    >
                        Get in Touch
                    </Center>
                </Center>
                <Center>
                    <Box>
                        <Text fontFamily={'heading2'} fontSize={['30px', '35px', '40px', '50px']}>Let's get in touch!</Text>
                    </Box>
                </Center>
                <Box height={['80px']}>
                </Box>
                <Flex mb={2}>
                    <Input
                        placeholder={placeholder}
                        value={value}
                        onChange={(e) => {
                            setValue(e.target.value)
                        }}
                        mr={2}
                    />
                    <Button onClick={onCopy}>{hasCopied ? 'Copied!' : 'Copy'}</Button>
                </Flex>
                <Editable placeholder='Paste here'>
                    <EditablePreview width='100%' />
                    <EditableInput />
                </Editable>



            </Container >
        </ChakraProvider >
    );
}

export default GetInTouch;
